import React from 'react';
import axios from 'axios';

export default class Login extends React.Component{
  state = {
    email: '',
    password: ''
  };

  url = this.props.baseUrl + '/auth/login';

  handleSubmit = (event) => {
    event.preventDefault();
    axios.post(this.url, this.state)
      .then((res) => {
        console.log(res);
        localStorage.setItem("my-cookie", res.data);
      })
      .catch((error) => {
        console.log(error)
      });
  };

  handleOnChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  render() {
    return(
      <form onSubmit={this.handleSubmit}>
        <h1>Login to your account:</h1>
        <label>Email: </label>
        <input onChange={this.handleOnChange} name="email" placeholder="freddie.mercury@gmail.com" size="35" value={this.state.email}/>
        <br/>
        <label>Password: </label>
        <input onChange={this.handleOnChange} name="password" placeholder="queen_rox" size="35" value={this.state.password}/>
        <br/>
        <input type='submit'/>
      </form>
    )
  };
};
