// This function will be used to get cookies from the browser
function getCookie(name){
  var cookieArray = document.cookie.split(';');
  for(var i=0; i < cookieArray.length; i++){
    var cookieKeyValuePair = cookieArray[i].split('=');
    if(name === cookieKeyValuePair[0]) {
      return(cookieKeyValuePair[1]);
    };
  };
  return(null);
};

// This function will be used to determine whether the user has access to the content
function isAuthenticated() {
// Check cookies if cookie exists give access to next page
  var checkCookie = getCookie('my-token');
  if(checkCookie != null){
    return true;
  } else {
    return false;
  };
};

export default isAuthenticated;
