import React, { useState } from 'react';
import axios from 'axios';
import { useHistory, useLocation } from 'react-router-dom';

export const LoginPage = (props) => {
  let state = {
    email: '',
    password: ''
  };
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  let url = props.baseUrl + '/auth/login';
  let location = useLocation();
  let history = useHistory();

// Create a cookie with the JWT
  function handleSubmit(event){
    event.preventDefault();
    axios.post(url, state)
      .then((res) => {
        console.log(res);
        // localStorage.setItem("my_cookie", res.data);
        document.cookie = 'my-token='+res.data.token+'; max-age=60;';
      })
      .catch((error) => {
        console.log(error);
      });
  };

  function handleOnChange(event){
    console.log(event.target.email);
    // setEmail(
    //   event.
    // );
  };

  function handleOnClick(){
    console.log(history);
    console.log(location);
  };

    return(
      <form onSubmit={handleSubmit}>
        <h1>Login to your account:</h1>
        <label>Email: </label>
        <input onChange={handleOnChange} name="email" placeholder="freddie.mercury@gmail.com" size="35" value={state.email}/>
        <br/>
        <label>Password: </label>
        <input onChange={handleOnChange} name="password" placeholder="queen_rox" size="35" value={state.password}/>
        <br/>
        <input onClick={handleOnClick} type='submit'/>
      </form>
    );
};
