import React from 'react';
import axios from 'axios';
import { useHistory, useLocation } from 'react-router-dom';

export default class Login extends React.Component{
  state = {
    email: '',
    password: ''
  };

  url = this.props.baseUrl + '/auth/login';

// Create a cookie with the JWT
  handleSubmit = (event) => {
    event.preventDefault();
    axios.post(this.url, this.state)
      .then((res) => {
        console.log(res);
        // localStorage.setItem("my_cookie", res.data);
        document.cookie = 'my-token='+res.data.token+'; max-age=60;';
      })
      .catch((error) => {
        console.log(error);
      });
  };

  handleOnChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleOnClick = () => {
    let location = useLocation();
    let history = useHistory();

    let { from } = location.state || { from: { pathname: "/" } };

    console.log(from);
  };

  render() {
    return(
      <form onSubmit={this.handleSubmit}>
        <h1>Login to your account:</h1>
        <label>Email: </label>
        <input onChange={this.handleOnChange} name="email" placeholder="freddie.mercury@gmail.com" size="35" value={this.state.email}/>
        <br/>
        <label>Password: </label>
        <input onChange={this.handleOnChange} name="password" placeholder="queen_rox" size="35" value={this.state.password}/>
        <br/>
        <input onClick={this.handleOnClick} type='submit'/>
      </form>
    )
  };
};
