import React from 'react';
import axios from 'axios';
import { useHistory, useLocation } from 'react-router-dom';

export const LoginPage = (props) => {
  let state = {
    email: '',
    password: ''
  };

  let url = props.baseUrl + '/auth/login';
  let location = useLocation()

// Create a cookie with the JWT
  function handleSubmit(event){
    event.preventDefault();
    axios.post(url, state)
      .then((res) => {
        console.log(res);
        // localStorage.setItem("my_cookie", res.data);
        document.cookie = 'my-token='+res.data.token+'; max-age=60;';
      })
      .catch((error) => {
        console.log(error);
      });
  };

  // function handleOnChange(event){
  //   setState({
  //     [event.target.name]: event.target.value
  //   });
  // };

  function handleOnClick(){
    console.log(location);
  };

    return(
      <form onSubmit={handleSubmit}>
        <h1>Login to your account:</h1>
        <label>Email: </label>
        <input  name="email" placeholder="freddie.mercury@gmail.com" size="35" value={state.email}/>
        <br/>
        <label>Password: </label>
        <input  name="password" placeholder="queen_rox" size="35" value={state.password}/>
        <br/>
        <input onClick={handleOnClick} type='submit'/>
      </form>
    );
};
